var connection = null;
var mongodb = require('mongodb');

exports.withDb = function(callback) {
    if (connection) return connection;
    else {
        var host = new mongodb.Server('127.0.0.1', 27017, { });
        var options = {safe: true, auto_reconnect: true};
        mongodb.Db('cubes', host, options).open(function (e, c) {
            connection = c;
            callback(e, c);
        });
    }
}

exports.initializeDb = function(callback) {
    conn.withDb(function (err1, db) {
        if (err1) {
            callback(err1);
            return;
        }
        var accounts = db.collection('accounts');
        accounts.ensureIndex({'username': 1}, {'unique': true});
        
        var puzzles = db.collection('puzzles');
        puzzles.ensureIndex({'account': 1, 'id': 1}, {'unique': true});
        
        var sessions = db.collection('sessions');
        sessions.ensureIndex({'account': 1, 'id': 1}, {'unique': true});
        
        callback(null);
    });
}
